mapper.select:
select t.id,t.entpId,t.portId,t.time,t.pollutantTypeId,t.pCou,t.entp,t.port,
MAX(CASE e.污染物编码ID WHEN #{factor.id} THEN e.Avg END) AS ${factor.code+'avg'}, MAX(CASE e.污染物编码ID WHEN #{factor.id} THEN e.Cou END) AS ${factor.code+'cou'}, MAX(CASE e.污染物编码ID WHEN #{factor.id} THEN e.ZSAvg END) AS ${factor.code+'avgzs'}
from (select m.ID as id, m.企业基本信息ID as entpId, m.设备信息ID as portId, m.监测时间 as time, m.污染物类别ID as pollutantTypeId, m.时段量 as pCou, e.企业名称 as entp, p.排放口名称 as port from MonthDataMain as m left join EnterpriseInfor as e on e.ID = m.企业基本信息ID left join EquipmentInfor as p on p.ID = m.设备信息ID
m.企业基本信息ID = #{entpId}
AND m.设备信息ID = #{portId}
AND m.监测时间 >= #{startTime}
AND m.监测时间 <= #{endTime}
) as t left join MonthDataDetail as e on t.id = e.主月数据ID group by t.id,t.entpId,t.portId,t.time,t.pollutantTypeId,t.pCou,t.entp,t.port order by t.id desc
